/* This file handles signals, which are asynchronous events and are generally
 * a messy and unpleasant business.  Signals can be generated by the KILL
 * system call, or from the keyboard (SIGINT) or from the clock (SIGALRM).
 * In all cases control eventually passes to check_sig() to see which processes
 * can be signaled.  The actual signaling is done by sig_proc().
 *
 * The entry points into this file are:
 *   do_sigaction:	perform the SIGACTION system call
 *   do_sigpending:	perform the SIGPENDING system call
 *   do_sigprocmask:	perform the SIGPROCMASK system call
 *   do_sigreturn:	perform the SIGRETURN system call
 *   do_sigsuspend:	perform the SIGSUSPEND system call
 *   do_kill:		perform the KILL system call
 *   process_ksig:	process a signal an behalf of the kernel
 *   sig_proc:		interrupt or terminate a signaled process
 *   check_sig:		check which processes to signal with sig_proc()
 *   check_pending:	check if a pending signal can now be delivered
 *   restart_sigs: 	restart signal work after finishing a VFS call
 */

#include "pm.h"
#include <sys/stat.h>
#include <sys/ptrace.h>
#include <minix/callnr.h>
#include <minix/endpoint.h>
#include <minix/com.h>
#include <minix/vm.h>
#include <signal.h>
#include <sys/resource.h>
#include <assert.h>
#include "mproc.h"

static int unpause(struct mproc *rmp);
static int sig_send(struct mproc *rmp, int signo);
static void sig_proc_exit(struct mproc *rmp, int signo);

/*===========================================================================*
 *				do_sigaction				     *
 *===========================================================================*/
int do_sigaction(void)
{
    const int sig_nr = m_in.m_lc_pm_sig.nr;
    int r;

    assert(!(mp->mp_flags & (PROC_STOPPED | VFS_CALL | UNPAUSED | EVENT_CALL)));

    if (sig_nr < 1 || sig_nr >= _NSIG) {
        return EINVAL;
    }
    if (sig_nr == SIGKILL) {
        return OK;
    }

    if (m_in.m_lc_pm_sig.oact != 0) {
        struct sigaction *old_act_p = &mp->mp_sigact[sig_nr];
        r = sys_datacopy(PM_PROC_NR, (vir_bytes)old_act_p, who_e,
                         m_in.m_lc_pm_sig.oact,
                         (phys_bytes)sizeof(*old_act_p));
        if (r != OK) {
            return r;
        }
    }

    if (m_in.m_lc_pm_sig.act == 0) {
        return OK;
    }

    struct sigaction new_act;
    r = sys_datacopy(who_e, m_in.m_lc_pm_sig.act, PM_PROC_NR,
                     (vir_bytes)&new_act, (phys_bytes)sizeof(new_act));
    if (r != OK) {
        return r;
    }

    sigdelset(&mp->mp_ignore, sig_nr);
    sigdelset(&mp->mp_catch, sig_nr);

    if (new_act.sa_handler == SIG_IGN) {
        sigaddset(&mp->mp_ignore, sig_nr);
        sigdelset(&mp->mp_sigpending, sig_nr);
        sigdelset(&mp->mp_ksigpending, sig_nr);
    } else if (new_act.sa_handler != SIG_DFL) {
        sigaddset(&mp->mp_catch, sig_nr);
    }

    mp->mp_sigact[sig_nr].sa_handler = new_act.sa_handler;

    sigdelset(&new_act.sa_mask, SIGKILL);
    sigdelset(&new_act.sa_mask, SIGSTOP);
    mp->mp_sigact[sig_nr].sa_mask = new_act.sa_mask;

    mp->mp_sigact[sig_nr].sa_flags = new_act.sa_flags;
    mp->mp_sigreturn = m_in.m_lc_pm_sig.ret;

    return OK;
}

/*===========================================================================*
 *				do_sigpending                                *
 *===========================================================================*/
int do_sigpending(void)
{
    const unsigned int forbidden_flags = PROC_STOPPED | VFS_CALL | UNPAUSED | EVENT_CALL;

    if ((mp->mp_flags & forbidden_flags) != 0) {
        return EINVAL;
    }

    mp->mp_reply.m_pm_lc_sigset.set = mp->mp_sigpending;
    return OK;
}

/*===========================================================================*
 *				do_sigprocmask                               *
 *===========================================================================*/
int do_sigprocmask(void)
{
    sigset_t set;
    int how;

    assert(!(mp->mp_flags & (PROC_STOPPED | VFS_CALL | UNPAUSED | EVENT_CALL)));

    set = m_in.m_lc_pm_sigset.set;
    how = m_in.m_lc_pm_sigset.how;
    mp->mp_reply.m_pm_lc_sigset.set = mp->mp_sigmask;

    if (how == SIG_BLOCK || how == SIG_SETMASK) {
        sigdelset(&set, SIGKILL);
        sigdelset(&set, SIGSTOP);
    }

    switch (how) {
    case SIG_BLOCK:
        for (int i = 1; i < _NSIG; i++) {
            if (sigismember(&set, i)) {
                sigaddset(&mp->mp_sigmask, i);
            }
        }
        break;

    case SIG_UNBLOCK:
        for (int i = 1; i < _NSIG; i++) {
            if (sigismember(&set, i)) {
                sigdelset(&mp->mp_sigmask, i);
            }
        }
        check_pending(mp);
        break;

    case SIG_SETMASK:
        mp->mp_sigmask = set;
        check_pending(mp);
        break;

    case SIG_INQUIRE:
        break;

    default:
        return EINVAL;
    }

    return OK;
}

/*===========================================================================*
 *				do_sigsuspend                                *
 *===========================================================================*/
int do_sigsuspend(void)
{
    const unsigned int invalid_flags = PROC_STOPPED | VFS_CALL | UNPAUSED | EVENT_CALL;
    assert(!(mp->mp_flags & invalid_flags));

    mp->mp_sigmask2 = mp->mp_sigmask;

    sigset_t new_mask = m_in.m_lc_pm_sigset.set;

    sigdelset(&new_mask, SIGKILL);
    sigdelset(&new_mask, SIGSTOP);

    mp->mp_sigmask = new_mask;
    mp->mp_flags |= SIGSUSPENDED;

    check_pending(mp);

    return SUSPEND;
}

/*===========================================================================*
 *				do_sigreturn				     *
 *===========================================================================*/
int do_sigreturn(void)
{
    assert(!(mp->mp_flags & (PROC_STOPPED | VFS_CALL | UNPAUSED | EVENT_CALL)));

    mp->mp_sigmask = m_in.m_lc_pm_sigset.set;
    sigdelset(&mp->mp_sigmask, SIGKILL);
    sigdelset(&mp->mp_sigmask, SIGSTOP);

    const int r = sys_sigreturn(who_e, (struct sigmsg *)m_in.m_lc_pm_sigset.ctx);
    check_pending(mp);

    return r;
}

/*===========================================================================*
 *				do_kill					     *
 *===========================================================================*/
#include <sys/types.h>
#include <stdbool.h>

/* Assume m_in and check_sig are defined elsewhere, along with necessary types. */
extern int check_sig(pid_t pid, int sig_nr, bool is_kernel_signal);
extern struct {
    struct {
        pid_t pid;
        int nr;
    } m_lc_pm_sig;
} m_in;

int do_kill(void)
{
    const pid_t pid = m_in.m_lc_pm_sig.pid;
    const int signal_number = m_in.m_lc_pm_sig.nr;
    const bool is_kernel_signal = false;

    return check_sig(pid, signal_number, is_kernel_signal);
}

/*===========================================================================*
 *			      do_srv_kill				     *
 *===========================================================================*/
int do_srv_kill(void)
{
    if (mp->mp_endpoint != RS_PROC_NR) {
        return EPERM;
    }

    const int pid = m_in.m_rs_pm_srv_kill.pid;
    const int signo = m_in.m_rs_pm_srv_kill.nr;
    const int is_kernel_signal = TRUE;

    return check_sig(pid, signo, is_kernel_signal);
}

/*===========================================================================*
 *				stop_proc				     *
 *===========================================================================*/
static int stop_proc(struct mproc *rmp, int may_delay)
{
	int r;

	assert(!(rmp->mp_flags & (PROC_STOPPED | DELAY_CALL | UNPAUSED)));

	r = sys_delay_stop(rmp->mp_endpoint);

	if (r == OK) {
		rmp->mp_flags |= PROC_STOPPED;
		return TRUE;
	}

	if (r == EBUSY) {
		if (!may_delay)
			panic("stop_proc: unexpected delay call");

		rmp->mp_flags |= DELAY_CALL;
		return FALSE;
	}

	panic("sys_delay_stop failed: %d", r);
}

/*===========================================================================*
 *				try_resume_proc				     *
 *===========================================================================*/
static void try_resume_proc(struct mproc *rmp)
{
    assert(rmp->mp_flags & PROC_STOPPED);

    if (rmp->mp_flags & (VFS_CALL | EVENT_CALL | EXITING)) {
        return;
    }

    const int r = sys_resume(rmp->mp_endpoint);
    if (r != OK) {
        printf("sys_resume for endpoint %d failed with error %d\n",
            rmp->mp_endpoint, r);
        return;
    }

    rmp->mp_flags &= ~(PROC_STOPPED | UNPAUSED);
}

/*===========================================================================*
 *				process_ksig				     *
 *===========================================================================*/
static int is_process_alive(const struct mproc *rmp)
{
    return (rmp->mp_flags & (IN_USE | EXITING)) == IN_USE;
}

static void handle_sigsndelay(struct mproc *rmp)
{
    if (!(rmp->mp_flags & DELAY_CALL)) {
        return;
    }

    rmp->mp_flags &= ~DELAY_CALL;

    assert(!(rmp->mp_flags & PROC_STOPPED));

    if (rmp->mp_flags & (VFS_CALL | EVENT_CALL)) {
        stop_proc(rmp, FALSE /*may_delay*/);
        return;
    }

    check_pending(rmp);

    assert(!(rmp->mp_flags & DELAY_CALL));
}

int process_ksig(endpoint_t proc_nr_e, int signo)
{
    struct mproc *rmp;
    int proc_nr;
    pid_t id;

    if (pm_isokendpt(proc_nr_e, &proc_nr) != OK) {
        printf("PM: process_ksig: %d?? not ok\n", proc_nr_e);
        return EDEADEPT;
    }

    rmp = &mproc[proc_nr];
    if (!is_process_alive(rmp)) {
        return EDEADEPT;
    }

    mp = &mproc[0];
    mp->mp_procgrp = rmp->mp_procgrp;

    switch (signo) {
        case SIGINT:
        case SIGQUIT:
        case SIGWINCH:
        case SIGINFO:
            id = 0;
            break;
        case SIGVTALRM:
        case SIGPROF:
            check_vtimer(proc_nr, signo);
            /* fall-through */
        default:
            id = rmp->mp_pid;
            break;
    }

    check_sig(id, signo, TRUE /* ksig */);

    mp->mp_procgrp = 0;

    if (signo == SIGSNDELAY) {
        handle_sigsndelay(rmp);
    }

    return is_process_alive(rmp) ? OK : EDEADEPT;
}

/*===========================================================================*
 *				sig_proc				     *
 *===========================================================================*/
static void
pend_signal(struct mproc *rmp, int signo, int ksig)
{
	sigaddset(&rmp->mp_sigpending, signo);
	if (ksig) {
		sigaddset(&rmp->mp_ksigpending, signo);
	}
}

static void
handle_priv_proc_signal(struct mproc *rmp, int signo, int ksig)
{
	if (rmp->mp_endpoint == PM_PROC_NR) {
		return;
	}

	if (!ksig) {
		sys_kill(rmp->mp_endpoint, signo);
		return;
	}

	if (SIGS_IS_STACKTRACE(signo)) {
		sys_diagctl_stacktrace(rmp->mp_endpoint);
	}

	if (!SIGS_IS_TERMINATION(signo)) {
		message m;
		m.m_type = SIGS_SIGNAL_RECEIVED;
		m.m_pm_lsys_sigs_signal.num = signo;
		asynsend3(rmp->mp_endpoint, &m, AMF_NOREPLY);
	} else {
		sig_proc_exit(rmp, signo);
	}
}

void
sig_proc(
	register struct mproc *rmp,
	int signo,
	int trace,
	int ksig
)
{
	int slot = (int)(rmp - mproc);
	if ((rmp->mp_flags & (IN_USE | EXITING)) != IN_USE) {
		panic("PM: signal %d sent to exiting process %d\n", signo, slot);
	}

	if (trace && rmp->mp_tracer != NO_TRACER && signo != SIGKILL) {
		sigaddset(&rmp->mp_sigtrace, signo);
		if (!(rmp->mp_flags & TRACE_STOPPED)) {
			trace_stop(rmp, signo);
		}
		return;
	}

	if (rmp->mp_flags & (VFS_CALL | EVENT_CALL)) {
		pend_signal(rmp, signo, ksig);
		if (!(rmp->mp_flags & (PROC_STOPPED | DELAY_CALL))) {
			stop_proc(rmp, 0);
		}
		return;
	}

	if (rmp->mp_flags & PRIV_PROC) {
		handle_priv_proc_signal(rmp, signo, ksig);
		return;
	}

	const int is_ignored = sigismember(&rmp->mp_ignore, signo);
	const int is_blocked = sigismember(&rmp->mp_sigmask, signo);
	const int is_nonignorable = ksig && sigismember(&noign_sset, signo);
	const int can_be_handled = !(is_nonignorable && (is_ignored || is_blocked));

	if (can_be_handled) {
		if (is_ignored) {
			return;
		}

		if (is_blocked) {
			pend_signal(rmp, signo, ksig);
			return;
		}

		if ((rmp->mp_flags & TRACE_STOPPED) && signo != SIGKILL) {
			pend_signal(rmp, signo, ksig);
			return;
		}

		if (sigismember(&rmp->mp_catch, signo)) {
			if (!unpause(rmp)) {
				pend_signal(rmp, signo, ksig);
				return;
			}
			if (sig_send(rmp, signo)) {
				return;
			}
			printf("PM: %d can't catch signal %d - killing\n",
				rmp->mp_pid, signo);
		} else if (sigismember(&ign_sset, signo)) {
			return;
		}
	}

	sig_proc_exit(rmp, signo);
}

/*===========================================================================*
 *				sig_proc_exit				     *
 *===========================================================================*/
static void
sig_proc_exit(struct mproc *rmp, int signo)
{
	const int dump_core = sigismember(&core_sset, signo);

	rmp->mp_sigstatus = (char) signo;

	if (dump_core && !(rmp->mp_flags & PRIV_PROC)) {
		printf("PM: coredump signal %d for %d / %s\n", signo,
			rmp->mp_pid, rmp->mp_name);
		sys_diagctl_stacktrace(rmp->mp_endpoint);
	}

	exit_proc(rmp, 0, dump_core);
}

/*===========================================================================*
 *				check_sig				     *
 *===========================================================================*/
int check_sig(proc_id, signo, ksig)
pid_t proc_id;			/* pid of proc to sig, or 0 or -1, or -pgrp */
int signo;			/* signal to send to process (0 to _NSIG-1) */
int ksig;			/* non-zero means signal comes from kernel  */
{
/* Check to see if it is possible to send a signal.  The signal may have to be
 * sent to a group of processes.  This routine is invoked by the KILL system
 * call, and also when the kernel catches a DEL or other signal.
 */

  register struct mproc *rmp;
  int count;			/* count # of signals sent */
  int error_code;

  if (signo < 0 || signo >= _NSIG) return(EINVAL);

  /* Return EINVAL for attempts to send SIGKILL to INIT alone. */
  if (proc_id == INIT_PID && signo == SIGKILL) return(EINVAL);

  /* Signal RS first when broadcasting SIGTERM. */
  if (proc_id == -1 && signo == SIGTERM)
      sys_kill(RS_PROC_NR, signo);

  /* Search the proc table for processes to signal. Start from the end of the
   * table to analyze core system processes at the end when broadcasting.
   * (See forkexit.c about pid magic.)
   */
  count = 0;
  error_code = ESRCH;
  for (rmp = &mproc[NR_PROCS-1]; rmp >= &mproc[0]; rmp--) {
	if (!(rmp->mp_flags & IN_USE)) continue;

	/* Check for selection. */
	if (proc_id > 0 && proc_id != rmp->mp_pid) continue;
	if (proc_id == 0 && mp->mp_procgrp != rmp->mp_procgrp) continue;
	if (proc_id == -1 && rmp->mp_pid <= INIT_PID) continue;
	if (proc_id < -1 && rmp->mp_procgrp != -proc_id) continue;

	/* Do not kill servers and drivers when broadcasting SIGKILL. */
	if (proc_id == -1 && signo == SIGKILL &&
		(rmp->mp_flags & PRIV_PROC)) continue;

	/* Skip VM entirely as it might lead to a deadlock with its signal
	 * manager if the manager page faults at the same time.
	 */
	if (rmp->mp_endpoint == VM_PROC_NR) continue;

	/* Disallow lethal signals sent by user processes to sys processes. */
	if (!ksig && SIGS_IS_LETHAL(signo) && (rmp->mp_flags & PRIV_PROC)) {
	    error_code = EPERM;
	    continue;
	}

	/* Check for permission. */
	if (mp->mp_effuid != SUPER_USER
	    && mp->mp_realuid != rmp->mp_realuid
	    && mp->mp_effuid != rmp->mp_realuid
	    && mp->mp_realuid != rmp->mp_effuid
	    && mp->mp_effuid != rmp->mp_effuid) {
		error_code = EPERM;
		continue;
	}

	count++;
	if (signo == 0 || (rmp->mp_flags & EXITING)) continue;

	/* 'sig_proc' will handle the disposition of the signal.  The
	 * signal may be caught, blocked, ignored, or cause process
	 * termination, possibly with core dump.
	 */
	sig_proc(rmp, signo, TRUE /*trace*/, ksig);

	if (proc_id > 0) break;	/* only one process being signaled */
  }

  /* If the calling process has killed itself, don't reply. */
  if ((mp->mp_flags & (IN_USE | EXITING)) != IN_USE) return(SUSPEND);
  return(count > 0 ? OK : error_code);
}

/*===========================================================================*
 *				check_pending				     *
 *===========================================================================*/
void
check_pending(struct mproc *rmp)
{
	for (int i = 1; i < _NSIG; i++) {
		if (sigismember(&rmp->mp_sigpending, i) &&
		    !sigismember(&rmp->mp_sigmask, i)) {
			const int ksig = sigismember(&rmp->mp_ksigpending, i);

			sigdelset(&rmp->mp_sigpending, i);
			sigdelset(&rmp->mp_ksigpending, i);
			sig_proc(rmp, i, 0, ksig);

			if (rmp->mp_flags & (VFS_CALL | EVENT_CALL)) {
				assert(rmp->mp_flags & PROC_STOPPED);
				break;
			}
		}
	}
}

/*===========================================================================*
 *				restart_sigs				     *
 *===========================================================================*/
void
restart_sigs(struct mproc *rmp)
{
    if (rmp == NULL) {
        return;
    }

    if (rmp->mp_flags & (VFS_CALL | EVENT_CALL | EXITING)) {
        return;
    }

    if (rmp->mp_flags & TRACE_EXIT) {
        exit_proc(rmp, rmp->mp_exitstatus, FALSE);
    } else if (rmp->mp_flags & PROC_STOPPED) {
        assert(!(rmp->mp_flags & DELAY_CALL));

        check_pending(rmp);
        try_resume_proc(rmp);
    }
}

/*===========================================================================*
 *				unpause					     *
 *===========================================================================*/
static int
unpause(
	struct mproc *rmp
)
{
	message m;

	assert(!(rmp->mp_flags & (VFS_CALL | EVENT_CALL)));

	if (rmp->mp_flags & UNPAUSED) {
		assert((rmp->mp_flags & (DELAY_CALL | PROC_STOPPED)) == PROC_STOPPED);
		return TRUE;
	}

	if (rmp->mp_flags & DELAY_CALL) {
		return FALSE;
	}

	if (rmp->mp_flags & (WAITING | SIGSUSPENDED)) {
		stop_proc(rmp, FALSE);
		return TRUE;
	}

	if (!(rmp->mp_flags & PROC_STOPPED)) {
		if (!stop_proc(rmp, TRUE)) {
			return FALSE;
		}
	}

	memset(&m, 0, sizeof(m));
	m.m_type = VFS_PM_UNPAUSE;
	m.VFS_PM_ENDPT = rmp->mp_endpoint;

	tell_vfs(rmp, &m);

	return FALSE;
}

/*===========================================================================*
 *				sig_send				     *
 *===========================================================================*/
static int
sig_send(
	struct mproc *rmp,
	int signo
)
{
	struct sigmsg sigmsg;
	int r, sigflags, slot;

	assert(rmp != NULL);
	assert(signo > 0 && signo < _NSIG);
	assert(rmp->mp_flags & PROC_STOPPED);

	sigflags = rmp->mp_sigact[signo].sa_flags;
	slot = (int)(rmp - mproc);

	sigmsg.sm_mask = (rmp->mp_flags & SIGSUSPENDED) ?
		rmp->mp_sigmask2 : rmp->mp_sigmask;
	sigmsg.sm_signo = signo;
	sigmsg.sm_sighandler = (vir_bytes)rmp->mp_sigact[signo].sa_handler;
	sigmsg.sm_sigreturn = rmp->mp_sigreturn;

	for (int i = 1; i < _NSIG; i++) {
		if (sigismember(&rmp->mp_sigact[signo].sa_mask, i)) {
			sigaddset(&rmp->mp_sigmask, i);
		}
	}

	if (sigflags & SA_NODEFER) {
		sigdelset(&rmp->mp_sigmask, signo);
	} else {
		sigaddset(&rmp->mp_sigmask, signo);
	}

	if (sigflags & SA_RESETHAND) {
		sigdelset(&rmp->mp_catch, signo);
		rmp->mp_sigact[signo].sa_handler = SIG_DFL;
	}

	sigdelset(&rmp->mp_sigpending, signo);
	sigdelset(&rmp->mp_ksigpending, signo);

	r = sys_sigsend(rmp->mp_endpoint, &sigmsg);
	if (r != OK) {
		if (r == EFAULT || r == ENOMEM) {
			return(FALSE);
		}
		panic("sys_sigsend failed: %d", r);
	}

	if (rmp->mp_flags & (WAITING | SIGSUSPENDED)) {
		rmp->mp_flags &= ~(WAITING | SIGSUSPENDED);
		reply(slot, EINTR);
		assert(!(rmp->mp_flags & UNPAUSED));
		try_resume_proc(rmp);
		assert(!(rmp->mp_flags & PROC_STOPPED));
	} else {
		assert(rmp->mp_flags & UNPAUSED);
	}

	return(TRUE);
}
